import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  // 资源与工人状态
  const [resource, setResource] = useState(0);
  const [workers, setWorkers] = useState(0);
  // 新增：手动采集动画与禁用状态
  const [collecting, setCollecting] = useState(false);

  // 手动采集资源
  const collectResource = () => {
    setCollecting(true);
    setTimeout(() => {
      setResource(r => r + 1);
      setCollecting(false);
    }, 800); // 采集动画持续800ms
  };

  // 招募工人
  const hireWorker = () => {
    if (resource >= 10) {
      setResource(resource - 10);
      setWorkers(workers + 1);
    } else {
      alert("招募工人需要10资源！");
    }
  };

  // 自动采集逻辑（默认开启，无需按钮）
  useEffect(() => {
    if (workers > 0) {
      const interval = setInterval(() => {
        setResource(r => r + workers);
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [workers]);

  return (
    <div className="App" style={{ padding: 24, fontFamily: 'sans-serif' }}>
      <h1>资源采集器</h1>
      <div style={{ marginBottom: 16 }}>
        <span style={{ fontSize: 20 }}>资源：{resource}</span>
      </div>
      <div style={{ marginBottom: 16 }}>
        <span style={{ fontSize: 20 }}>工人：{workers}</span>
      </div>
      <button onClick={collectResource} disabled={collecting} style={{ marginRight: 12, padding: '8px 16px', position: 'relative', minWidth: 90, overflow: 'hidden', background: '#f0f0f0' }}>
        {collecting ? (
          <span style={{
            position: 'absolute',
            left: 0,
            top: 0,
            height: '100%',
            width: '100%',
            background: 'rgba(33,150,243,0.15)',
            zIndex: 0,
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <span style={{
              display: 'block',
              height: '100%',
              width: '100%',
              background: '#2196f3',
              transform: 'scaleX(0)',
              transformOrigin: 'left',
              animation: 'progressBarFill 0.8s linear forwards',
              borderRadius: 4,
              opacity: 0.8
            }} />
          </span>
        ) : null}
        <span style={{ position: 'relative', zIndex: 1, color: collecting ? '#2196f3' : 'inherit' }}>
          {collecting ? '采集中...' : '采集资源'}
        </span>
      </button>
      <button onClick={hireWorker} style={{ marginRight: 12, padding: '8px 16px' }}>招募工人（10资源）</button>
      <div style={{ marginTop: 32, color: '#888' }}>
        <p>每个工人每秒自动采集1资源。</p>
        <p>招募工人需要消耗10资源。</p>
      </div>
    </div>
  );
}

export default App;

<style>{`@keyframes spin { 0% { transform: rotate(0deg);} 100% { transform: rotate(360deg);} }\n@keyframes progressBarFill { 0% { transform: scaleX(0);} 100% { transform: scaleX(1);} }`}</style>
