import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  // 资源与工人状态
  const [resource, setResource] = useState(0);
  const [workers, setWorkers] = useState(0);
  const [autoCollecting, setAutoCollecting] = useState(false);

  // 手动采集资源
  const collectResource = () => {
    setResource(resource + 1);
  };

  // 招募工人
  const hireWorker = () => {
    if (resource >= 10) {
      setResource(resource - 10);
      setWorkers(workers + 1);
    } else {
      alert("招募工人需要10资源！");
    }
  };

  // 自动采集逻辑
  useEffect(() => {
    if (autoCollecting && workers > 0) {
      const interval = setInterval(() => {
        setResource(r => r + workers);
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [autoCollecting, workers]);

  return (
    <div className="App" style={{ padding: 24, fontFamily: 'sans-serif' }}>
      <h1>资源采集器</h1>
      <div style={{ marginBottom: 16 }}>
        <span style={{ fontSize: 20 }}>资源：{resource}</span>
      </div>
      <div style={{ marginBottom: 16 }}>
        <span style={{ fontSize: 20 }}>工人：{workers}</span>
      </div>
      <button onClick={collectResource} style={{ marginRight: 12, padding: '8px 16px' }}>采集资源</button>
      <button onClick={hireWorker} style={{ marginRight: 12, padding: '8px 16px' }}>招募工人（10资源）</button>
      <button
        onClick={() => setAutoCollecting(!autoCollecting)}
        style={{ padding: '8px 16px', background: autoCollecting ? '#4caf50' : '#2196f3', color: '#fff' }}
      >
        {autoCollecting ? "停止自动采集" : "开始自动采集"}
      </button>
      <div style={{ marginTop: 32, color: '#888' }}>
        <p>每个工人每秒自动采集1资源。</p>
        <p>招募工人需要消耗10资源。</p>
      </div>
    </div>
  );
}

export default App;
