import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  // 资源与工人状态
  const initialResources = [
    { name: '铁矿石', key: 'iron', value: 0, perSec: 0 },
    { name: '铜矿石', key: 'copper', value: 0, perSec: 0 },
    { name: '木材', key: 'wood', value: 0, perSec: 0 },
    { name: '石头', key: 'stone', value: 0, perSec: 0 },
    { name: '黄金', key: 'gold', value: 0, perSec: 0 }
  ];
  const [resources, setResources] = useState(initialResources);
  const [workers, setWorkers] = useState(0);
  const [level, setLevel] = useState(1);
  const [exp, setExp] = useState(0);
  const [collecting, setCollecting] = useState(false);
  const [cooldown, setCooldown] = useState(0);
  // 日志记录
  const [logs, setLogs] = useState(["游戏开始。"]);
  // 建筑系统
  const [buildings, setBuildings] = useState([]); // {type: 'house', count: 1}
  // 天气系统
  const [weather, setWeather] = useState({ temp: 10, desc: '晴朗', tick: 0 });
  const [dead, setDead] = useState(false);
  // 等级阶段与经验需求
  const levelStages = [
    { name: '初级', maxLevel: 3, exp: 100 },
    { name: '中级', maxLevel: 6, exp: 200 },
    { name: '高级', maxLevel: 99, exp: 500 }
  ];
  const getStage = (lv) => {
    if (lv < 3) return levelStages[0];
    if (lv < 6) return levelStages[1];
    return levelStages[2];
  };
  const getStageName = (lv) => getStage(lv).name;
  const getStageExp = (lv) => getStage(lv).exp;
  // 冷却时间随等级提升递减，最低0.5秒
  const getCooldownDuration = () => Math.max(2000 - (level - 1) * 150, 500);

  // 日志添加
  const addLog = (msg) => setLogs(logs => [msg, ...logs.slice(0, 49)]);
  // 手动采集资源（采集）
  const collectResource = () => {
    setCollecting(true);
    setCooldown(1);
    const cooldownDuration = getCooldownDuration();
    const start = Date.now();
    const timer = setInterval(() => {
      const elapsed = Date.now() - start;
      const progress = Math.max(0, 1 - elapsed / cooldownDuration);
      setCooldown(progress);
      if (elapsed >= cooldownDuration) {
        clearInterval(timer);
        setCooldown(0);
      }
    }, 16);
    setTimeout(() => {
      // 采集逻辑：根据等级和概率采集不同资源
      // 定义资源等级和掉落概率
      const resourceTiers = [
        // 初级资源
        [
          { key: 'wood', weight: 60 },
          { key: 'stone', weight: 40 }
        ],
        // 中级资源
        [
          { key: 'iron', weight: 60 },
          { key: 'copper', weight: 40 }
        ],
        // 高级资源
        [
          { key: 'gold', weight: 100 }
        ]
      ];
      // 根据等级解锁可采集资源
      let availableTiers = 1;
      if (level >= 3) availableTiers = 2;
      if (level >= 6) availableTiers = 3;
      // 合并可采集资源及概率
      let availableResources = [];
      for (let i = 0; i < availableTiers; i++) {
        availableResources = availableResources.concat(resourceTiers[i]);
      }
      // 概率加权采集
      const totalWeight = availableResources.reduce((sum, r) => sum + r.weight, 0);
      let rand = Math.random() * totalWeight;
      let selectedKey = availableResources[0].key;
      for (let i = 0; i < availableResources.length; i++) {
        rand -= availableResources[i].weight;
        if (rand <= 0) {
          selectedKey = availableResources[i].key;
          break;
        }
      }
      setResources(rs => rs.map(r => r.key === selectedKey ? { ...r, value: r.value + 1 } : r));
      addLog(`采集获得了${resources.find(r=>r.key===selectedKey)?.name||selectedKey}`);
      // 增加经验并处理升级
      setExp(prevExp => {
        let nextExp = prevExp + 10;
        let nextLevel = level;
        let stageExp = getStageExp(nextLevel);
        let changed = false;
        while (nextExp >= stageExp) {
          nextExp -= stageExp;
          nextLevel += 1;
          stageExp = getStageExp(nextLevel);
          changed = true;
        }
        if (changed) setLevel(nextLevel);
        return nextExp;
      });
      setCollecting(false);
    }, cooldownDuration);
  };

  // 招募工人（提升等级）
  const hireWorker = () => {
    if (resources[2].value >= 10) {
      setResources(rs => rs.map((r, idx) => {
        if(idx === 2) return { ...r, value: r.value - 10 };
        if(idx === 0) return { ...r, perSec: r.perSec + 0.2 };
        if(idx === 1) return { ...r, perSec: r.perSec + 0.2 };
        if(idx === 3) return { ...r, perSec: r.perSec + 0.2 };
        if(idx === 4) return { ...r, perSec: r.perSec + 0.1 };
        return r;
      }));
      setWorkers(w => w + 1);
      setLevel(lv => lv + 1);
      addLog('招募一名工人。');
    } else {
      alert('招募工人需要10木材！');
    }
  };
  // 建造房子
  const buildHouse = () => {
    if (resources[2].value >= 20 && resources[3].value >= 10) {
      setResources(rs => rs.map((r, idx) => {
        if(r.key==='wood') return { ...r, value: r.value - 20 };
        if(r.key==='stone') return { ...r, value: r.value - 10 };
        return r;
      }));
      setBuildings(bs => {
        const exist = bs.find(b=>b.type==='house');
        if(exist) return bs.map(b=>b.type==='house'?{...b,count:b.count+1}:b);
        return [...bs, {type:'house', count:1}];
      });
      addLog('建造了一间房子。');
    } else {
      alert('建造房子需要20木材和10石头！');
    }
  };
  // 添加木材按钮逻辑
  const addWood = () => {
    const wood = resources.find(r => r.key === 'wood');
    if (wood && wood.value >= 1) {
      setResources(rs => rs.map(r => r.key === 'wood' ? { ...r, value: r.value - 1 } : r));
      setWeather(w => ({ ...w, temp: w.temp + 1 }));
      addLog('消耗1木材，提升了室内温度！');
    } else {
      alert('木材不足，无法提升温度！');
    }
  };
  // 天气变化与生存检测
  useEffect(() => {
    if(dead) return;
    const interval = setInterval(() => {
      setWeather(w => {
        let tick = w.tick + 1;
        let temp = w.temp;
        let desc = w.desc;
        // 每10 tick变一次天气
        if(tick % 10 === 0) {
          const r = Math.random();
          if(r < 0.3) { temp = Math.max(-20, temp - Math.floor(Math.random()*8+3)); desc = '暴风雪'; }
          else if(r < 0.7) { temp = Math.max(-10, temp - Math.floor(Math.random()*4+1)); desc = '寒冷'; }
          else { temp = Math.min(25, temp + Math.floor(Math.random()*6)); desc = '晴朗'; }
          addLog(`天气变化：${desc}，温度${temp}℃`);
        }
        // 检查是否冻死
        let houseCount = buildings.find(b=>b.type==='house')?.count||0;
        if(temp <= -10 && houseCount === 0) {
          setDead(true);
          addLog('你被冻死了！游戏结束。');
        }
        return { temp, desc, tick };
      });
    }, 1000);
    return () => clearInterval(interval);
  }, [dead, buildings]);

  // 自动采集逻辑
  useEffect(() => {
    if (workers > 0) {
      const interval = setInterval(() => {
        setResources(rs => rs.map(r => r.perSec > 0 ? { ...r, value: +(r.value + r.perSec).toFixed(2) } : r));
        // 自动采集也增加经验
        setExp(prevExp => {
          let nextExp = prevExp + 5 * workers;
          let nextLevel = level;
          let stageExp = getStageExp(nextLevel);
          let changed = false;
          while (nextExp >= stageExp) {
            nextExp -= stageExp;
            nextLevel += 1;
            stageExp = getStageExp(nextLevel);
            changed = true;
          }
          if (changed) setLevel(nextLevel);
          return nextExp;
        });
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [workers, level]);

  return (
    <div className="App" style={{ padding: 24, fontFamily: 'sans-serif', display: 'flex', flexDirection: 'row', alignItems: 'flex-start' }}>
      {/* 日志区块 */}
      <div style={{ width: 220, minHeight: 400, marginRight: 24, background: '#f7f7f7', border: '1px solid #ccc', borderRadius: 6, padding: 12, textAlign: 'left', fontSize: 14, color: '#333', overflowY: 'auto', height: 480 }}>
        <div style={{ fontWeight: 'bold', marginBottom: 8 }}>日志记录</div>
        <ul style={{ padding: 0, margin: 0, listStyle: 'none' }}>
          {logs.map((log, idx) => <li key={idx} style={{ opacity: idx>20?0.5:1 }}>{log}</li>)}
        </ul>
      </div>
      {/* 主体内容 */}
      <div style={{ flex: 1 }}>
        <h1>打造末日安全屋</h1>
        <div style={{ marginBottom: 16 }}>
          <span style={{ fontSize: 18, marginRight: 16 }}>{getStageName(level)}：{level}/{getStageExp(level)}（经验：{exp}/{getStageExp(level)}）</span>
          <span style={{ fontSize: 18 }}>工人：{workers}</span>
        </div>
        <button onClick={collectResource} disabled={collecting || cooldown > 0 || dead} style={{ position: 'relative', marginRight: 12, padding: '8px 16px', overflow: 'hidden', width: 120 }}>
          {dead ? '已死亡' : (collecting || cooldown > 0 ? '采集中...' : '采集')}
          {(collecting || cooldown > 0) && !dead && (
            <span style={{
              position: 'absolute',
              left: 0,
              bottom: 0,
              height: 4,
              width: `${(cooldown > 0 ? cooldown : 1) * 100}%`,
              background: '#2196f3',
              transition: 'width 0.1s linear',
              zIndex: 1,
              borderRadius: 2,
              pointerEvents: 'none',
            }} />
          )}
        </button>
        <button onClick={hireWorker} disabled={dead} style={{ marginRight: 12, padding: '8px 16px' }}>招募工人（10木材，提升等级）</button>
        {/* 添加木材按钮，测试用 */}
        <button onClick={addWood} disabled={dead} style={{ marginRight: 12, padding: '8px 16px', background: '#2196f3', color: '#fff', border: 'none', borderRadius: 4, fontSize: 16, width: 120, height: 40, boxShadow: '0 2px 4px rgba(33,150,243,0.08)' }}>添加木材（-1）</button>
        {/* 建造房子按钮，等级3解锁 */}
        {level >= 3 && <button onClick={buildHouse} disabled={dead} style={{ marginRight: 12, padding: '8px 16px' }}>建造房子（20木材+10石头）</button>}
        {/* 展示建筑 */}
        <div style={{ marginTop: 16, marginBottom: 8 }}>
          {buildings.length > 0 && (
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>建筑：</div>
          )}
          {buildings.map(b => (
            <div key={b.type}>{b.type==='house'?`房子 x${b.count}`:b.type}</div>
          ))}
        </div>
        {/* 天气显示 */}
        <div style={{ marginBottom: 8, color: weather.temp<=-10?'#c00':'#2196f3', fontWeight: 'bold' }}>
          天气：{weather.desc}，温度：{weather.temp}℃
        </div>
        <div style={{ marginTop: 32, display: 'inline-block', border: '1px solid #aaa', borderRadius: 6, padding: 12, background: '#fff', width: 200 }}>
          <div style={{ fontWeight: 'bold', marginBottom: 4 }}>库存</div>
          <table style={{ borderCollapse: 'collapse', fontSize: 16 }}>
            <tbody>
              {resources.filter(r => r.value > 0).map((r, idx) => (
                <tr key={r.key}>
                  <td style={{ padding: '2px 8px', textAlign: 'right', color: idx === 0 ? '#333' : '#666' }}>{r.name}</td>
                  <td style={{ padding: '2px 8px', textAlign: 'right', fontWeight: idx === 0 ? 'bold' : 'normal' }}>
                    {Math.floor(r.value)}
                    {r.perSec > 0 && (
                      <span style={{ color: 'green', fontSize: 14, marginLeft: 2 }}>
                        (+{r.perSec})
                      </span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div style={{ marginTop: 24, color: '#888' }}>
          <p>每个工人每秒自动采集所有有增长速率的资源。</p>
          <p>招募工人消耗10木材并提升等级，等级越高采集冷却越短（最低0.5秒）。</p>
          <p>等级3解锁建造房子，房子可防止极寒天气冻死。</p>
          <p>天气系统：温度过低且无房子时会导致角色死亡。</p>
        </div>
      </div>
    </div>
  );
}

export default App;

